option.value:
@echo off
echo ========================================
echo   COPIANDO IMAGENES A ASSETS
echo ========================================
echo.

REM Ir a la carpeta main
cd /d C:\Users\Diego\AndroidStudioProjects\FullSound\app\src\main

REM Crear carpeta assets\images si no existe
if not exist "assets" mkdir assets
if not exist "assets\images" mkdir assets\images

echo [INFO] Carpeta assets\images creada
echo.

REM Verificar si existen las imágenes en res\images
if not exist "res\images" (
    echo [ERROR] La carpeta res\images no existe
    pause
    exit /b 1
)

echo [INFO] Copiando imagenes necesarias...
echo.

REM Copiar imágenes una por una
copy res\images\1.jpg assets\images\1.jpg >nul 2>&1
if %ERRORLEVEL% EQU 0 (echo [OK] 1.jpg copiado) else (echo [ERROR] 1.jpg no encontrado)

copy res\images\2.jpg assets\images\2.jpg >nul 2>&1
if %ERRORLEVEL% EQU 0 (echo [OK] 2.jpg copiado) else (echo [ERROR] 2.jpg no encontrado)

copy res\images\3.jpg assets\images\3.jpg >nul 2>&1
if %ERRORLEVEL% EQU 0 (echo [OK] 3.jpg copiado) else (echo [ERROR] 3.jpg no encontrado)

copy res\images\4.jpg assets\images\4.jpg >nul 2>&1
if %ERRORLEVEL% EQU 0 (echo [OK] 4.jpg copiado) else (echo [ERROR] 4.jpg no encontrado)

copy res\images\6.jpg assets\images\6.jpg >nul 2>&1
if %ERRORLEVEL% EQU 0 (echo [OK] 6.jpg copiado) else (echo [ERROR] 6.jpg no encontrado)

copy res\images\7.jpg assets\images\7.jpg >nul 2>&1
if %ERRORLEVEL% EQU 0 (echo [OK] 7.jpg copiado) else (echo [ERROR] 7.jpg no encontrado)

copy res\images\8.jpg assets\images\8.jpg >nul 2>&1
if %ERRORLEVEL% EQU 0 (echo [OK] 8.jpg copiado) else (echo [ERROR] 8.jpg no encontrado)

copy res\images\10.jpg assets\images\10.jpg >nul 2>&1
if %ERRORLEVEL% EQU 0 (echo [OK] 10.jpg copiado) else (echo [ERROR] 10.jpg no encontrado)

echo.
echo ========================================
echo   IMAGENES COPIADAS EXITOSAMENTE
echo ========================================
echo.
echo Ubicacion: app\src\main\assets\images\
echo.
echo Ahora las imagenes se mostraran correctamente en la app.
echo.
pause
